export default function Todo(props) {
  
    const updateTodo = async (todoId, todoStatus) => {
      const updatedStatus = !todoStatus;  
  
    
      const res = await fetch(`/api/todos/${todoId}`, {
        method: "PUT",
        body: JSON.stringify({ status: updatedStatus }),
        headers: {
          "Content-Type": "application/json",
        },
      });
      const json = await res.json();
  
      if (json.acknowledged) {
       
        props.setTodos((currentTodos) => {
          return currentTodos.map((currentTodo) => {
            if (currentTodo._id === todoId) {
              return { ...currentTodo, status: updatedStatus };
            }
            return currentTodo;
          });
        });
      }
    };
  

    const deleteTodo = async (todoId) => {
      const res = await fetch(`/api/todos/${todoId}`, {
        method: "DELETE",
      });
      const json = await res.json();
  
      if (json.acknowledged) {
       
        props.setTodos((currentTodos) =>
          currentTodos.filter((todo) => todo._id !== todoId)
        );
      }
    };
  
    return (
      <div className="todo">
        <p>{props.todo.todo}</p>
        <div className="mutations">
          <button
            className="todo__status"
            onClick={() => updateTodo(props.todo._id, props.todo.status)} 
          >
            {props.todo.status ? "‚òë" : "‚òê"}  
          </button>
          <button
            className="todo__delete"
            onClick={() => deleteTodo(props.todo._id)}
          >
            üóëÔ∏è
          </button>
        </div>
      </div>
    );
  }
